INSERT INTO `_tickets_mode` (`mode_id`, `mode_alias`, `mode_name`, `mode_order`, `mode_sql`, `mode_sql_total`) VALUES
(1, 'all', 'Todos', 10, 'SELECT t.*, user_firstname, user_lastname\r\n				FROM _tickets t, _members m\r\n				WHERE t.ticket_deleted = 0\r\n					AND t.ticket_group IN ({v_group})\r\n					AND t.ticket_contact = m.user_id\r\n				ORDER BY t.ticket_lastreply DESC\r\n				LIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\n				FROM _tickets t\r\n				WHERE t.ticket_deleted = 0\r\n					AND t.ticket_group IN ({v_group})\r\n				ORDER BY t.ticket_lastreply'),
(2, 'all_opened', 'Todos abiertos', 20, 'SELECT t.*, c.cat_name, user_firstname, user_lastname\r\n				FROM _tickets t, _tickets_cat c, _members m\r\n				WHERE t.ticket_cat = c.cat_id\r\n					AND t.ticket_deleted = 0\r\n					AND t.ticket_group IN ({v_group})\r\n					AND t.ticket_contact = m.user_id\r\n					AND t.ticket_status <> {v_closed}\r\n				ORDER BY t.ticket_lastreply DESC\r\n				LIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\n				FROM _tickets t, _tickets_cat c\r\n				WHERE t.ticket_cat = c.cat_id\r\n					AND t.ticket_deleted = 0\r\n					AND t.ticket_group IN ({v_group})\r\n					AND t.ticket_status <> {v_closed}\r\n				ORDER BY t.ticket_lastreply'),
(3, 'self', 'Asignadas a m&iacute;', 30, 'SELECT t.*, c.cat_name, user_firstname, user_lastname\r\n					FROM _tickets t, _tickets_cat c, _tickets_assign a, _members m\r\n					WHERE t.ticket_cat = c.cat_id\r\n						AND t.ticket_deleted = 0\r\n						AND a.user_id = {v_userid}\r\nAND a.user_id = m.user_id\r\n						AND a.assign_ticket = t.ticket_id\r\n						ORDER BY t.ticket_lastreply DESC\r\n					LIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\n					FROM _tickets t, _tickets_assign a, _tickets_cat c, _members m\r\n					WHERE t.ticket_cat = c.cat_id\r\n						AND t.ticket_deleted = 0\r\n						AND a.user_id = {v_userid}\r\nAND a.user_id = m.user_id\r\n						AND a.assign_ticket = t.ticket_id'),
(4, 'self_opened', 'Creadas por m&iacute; pendientes', 55, 'SELECT t.*, c.cat_name, user_firstname, user_lastname\r\n					FROM _tickets t, _tickets_cat c, _tickets_assign a, _members m\r\n					WHERE t.ticket_cat = c.cat_id\r\n						AND t.ticket_deleted = 0\r\n						AND t.ticket_group IN ({v_group})\r\n						AND m.user_id = t.ticket_contact\r\n						AND a.user_id = {v_userid}\r\n						AND a.assign_ticket = t.ticket_id\r\n						AND t.ticket_status <> {v_closed}\r\n					ORDER BY t.ticket_lastreply DESC\r\n					LIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\n					FROM _tickets t, _tickets_assign a\r\n					WHERE t.ticket_id = a.assign_ticket\r\n						AND t.ticket_deleted = 0\r\n						AND t.ticket_group IN ({v_group})\r\n						AND t.ticket_status <> {v_closed}\r\n						AND a.user_id = {v_userid}'),
(5, 'own', 'Creadas por m&iacute;', 50, 'SELECT t.*, c.cat_name, user_firstname, user_lastname\r\n					FROM _tickets t, _tickets_cat c, _members m\r\n					WHERE t.ticket_cat = c.cat_id\r\n						AND m.user_id = {v_userid}\r\n						AND t.ticket_contact = m.user_id\r\n						AND t.ticket_deleted = 0\r\n							ORDER BY t.ticket_lastreply DESC\r\n					LIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\n					FROM _tickets\r\n					WHERE ticket_contact = {v_userid}\r\n						AND ticket_deleted = 0'),
(6, 'closed', 'Completados', 60, 'SELECT t.*, c.cat_name, user_firstname, user_lastname\r\n               FROM _tickets t, _tickets_cat c, _members m\r\n               WHERE t.ticket_cat = c.cat_id\r\n                   AND t.ticket_deleted = 0\r\n                   AND t.ticket_group IN ({v_group})\r\n                   AND t.ticket_contact = m.user_id\r\n                   AND t.ticket_status = {v_closed}\r\n               ORDER BY t.ticket_lastreply DESC\r\n               LIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\n               FROM _tickets t, _tickets_cat c\r\n               WHERE t.ticket_cat = c.cat_id\r\n                   AND t.ticket_deleted = 0\r\n                   AND t.ticket_group IN ({v_group})\r\n                   AND t.ticket_status = {v_closed}\r\n               ORDER BY t.ticket_lastreply'),
(7, 'deleted', 'Eliminados', 70, 'SELECT t.*, c.cat_name, user_firstname, user_lastname\r\n					FROM _tickets t, _tickets_cat c, _members m\r\n					WHERE t.ticket_cat = c.cat_id\r\n						AND m.user_id = t.ticket_contact\r\n						AND t.ticket_deleted = 1\r\n						AND t.ticket_group IN ({v_group})\r\n					ORDER BY t.ticket_lastreply DESC\r\n					LIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\n					FROM _tickets\r\n					WHERE ticket_deleted = 1\r\n						AND ticket_group IN ({v_group})'),
(8, 'selff_opened', 'Asignadas a m&iacute; pendientes', 40, 'SELECT t.*, c.cat_name, user_firstname, user_lastname\r\nFROM _tickets t, _tickets_cat c, _tickets_assign a, _members m\r\nWHERE t.ticket_cat = c.cat_id\r\nAND t.ticket_deleted = 0\r\nAND a.user_id = {v_userid}\r\nAND a.user_id = m.user_id\r\nAND a.assign_ticket = t.ticket_id\r\nAND t.ticket_status <> {v_closed}\r\nORDER BY t.ticket_lastreply DESC\r\nLIMIT {v_limit_start}, {v_limit_end}', 'SELECT COUNT(ticket_id) AS total\r\nFROM _tickets t, _tickets_assign a, _tickets_cat c, _members m\r\nWHERE t.ticket_cat = c.cat_id\r\nAND t.ticket_deleted = 0\r\nAND a.user_id = {v_userid}\r\nAND a.user_id = m.user_id\r\nAND a.assign_ticket = t.ticket_id\r\nAND t.ticket_status <> {v_closed}');
